DROP TRIGGER IF EXISTS inspecao_registro_delete ON inspecao_registro CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_inspecao_registro_delete() RETURNS trigger AS $BODY$
DECLARE

qt_existe_w		bigint;

BEGIN

if (OLD.ie_origem = 'NF') then

	update	nota_fiscal_item
	set	nr_seq_inspecao  = NULL
	where	nr_sequencia in (
		SELECT	nr_sequencia
		from	nota_fiscal
		where	nr_seq_reg_inspecao = OLD.nr_sequencia);

	update	nota_fiscal
	set	nr_seq_reg_inspecao  = NULL
	where	nr_seq_reg_inspecao = OLD.nr_sequencia;

end if;

RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
