CREATE OR REPLACE FUNCTION trigger_fct_kit_estoque_comp_insert() RETURNS trigger AS $BODY$
declare

dt_utilizacao_w        timestamp;
BEGIN
  BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S') then

	NEW.ds_stack := substr('Usuario=' || NEW.nm_usuario || ' Data=' || NEW.dt_atualizacao || ' Stack=' || dbms_utility.format_call_stack,1,2000);

	BEGIN
		select dt_utilizacao
		into STRICT dt_utilizacao_w
		from kit_estoque
		where nr_sequencia = NEW.nr_seq_kit_estoque;
	exception when no_data_found then
		dt_utilizacao_w := null;
	end;

	if (dt_utilizacao_w is not null) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(1193654);
	end if;

end if;

  END;
RETURN NEW;
end
