DROP TRIGGER IF EXISTS kv_receita_farmacia_atual ON kv_receita_farmacia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_kv_receita_farmacia_atual() RETURNS trigger AS $BODY$
declare
qt_reg_w	bigint;
BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
if (coalesce(OLD.DT_RECEITA,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_RECEITA) and (NEW.DT_RECEITA is not null) then 
	NEW.ds_utc		:= obter_data_utc(NEW.DT_RECEITA, 'HV');	
end if;
 
if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then 
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;
 
 
<<Final>> 
qt_reg_w	:= 0;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_kv_receita_farmacia_atual() FROM PUBLIC;

CREATE TRIGGER kv_receita_farmacia_atual
	BEFORE INSERT OR UPDATE ON kv_receita_farmacia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_kv_receita_farmacia_atual();

