CREATE OR REPLACE FUNCTION trigger_fct_lab_grupo_impressao_update() RETURNS trigger AS $BODY$
declare
nr_sequencia_w	bigint;
dt_sequencia_w	timestamp;

BEGIN

if (NEW.ie_regra_sequencia is not null) then
	if (substr(NEW.ie_regra_sequencia,1,1) = 'D') then
		dt_sequencia_w := trunc(LOCALTIMESTAMP,'dd');
	elsif (substr(NEW.ie_regra_sequencia,1,1) = 'S') then
		dt_sequencia_w := trunc(LOCALTIMESTAMP,'day');
	elsif (substr(NEW.ie_regra_sequencia,1,1) = 'M') then
		dt_sequencia_w := trunc(LOCALTIMESTAMP,'month');
	elsif (substr(NEW.ie_regra_sequencia,1,1) = 'A') then
		dt_sequencia_w := trunc(LOCALTIMESTAMP,'year');
	elsif (substr(NEW.ie_regra_sequencia,1,1) = 'G') then
		dt_sequencia_w := trunc(LOCALTIMESTAMP,'year');
	end if;
	insert into lab_seq_grupo_imp(	nr_sequencia, nr_seq_grupo_imp, dt_sequencia,
					nr_valor_seq, dt_atualizacao, nm_usuario)
	values (nextval('lab_seq_grupo_imp_seq'), NEW.nr_sequencia, dt_sequencia_w,
		coalesce(NEW.nr_seq_inic,'0'), LOCALTIMESTAMP, NEW.nm_usuario);
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
