DROP TRIGGER IF EXISTS lab_interface_ativa_update ON lab_interface_ativa CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_lab_interface_ativa_update() RETURNS trigger AS $BODY$
declare

active_interfaces_count_w bigint;
cd_interface_w 			 	bigint;
cd_message_w				bigint;

BEGIN

	cd_message_w := 0;

	if (NEW.nr_seq_envio is null AND NEW.nr_seq_retorno is null) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(338086);
	end if;

	if (NEW.ie_tipo <> OLD.ie_tipo) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(338039);
	end if;

	if ((OLD.ie_situacao = 'I' AND NEW.ie_situacao = 'A')
		or (NEW.ie_situacao = 'A' AND NEW.nr_seq_retorno <> OLD.nr_seq_retorno)
		or (NEW.ie_situacao = 'A' AND NEW.nr_seq_envio <> OLD.nr_seq_envio)) then

		select 	get_active_interface_count(NEW.nr_seq_retorno, NEW.nr_seq_retorno)
		into STRICT cd_message_w
		;

		if (cd_message_w > 0) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(cd_message_w);
		end if;

	end if;
RETURN NEW;
end
$BODY$
