declare

ie_existe_criterio_exame_w	varchar(1);

pragma autonomous_transaction;

BEGIN

select 	max(a.nr_sequencia)
into STRICT	ie_existe_criterio_exame_w
from    exame_lab_padrao a,
	lab_valor_padrao_criterio b
where 	a.nr_sequencia 	= b.nr_seq_padrao
and	a.nr_seq_exame 	= b.nr_seq_exame
and	b.nr_seq_exame 	= NEW.nr_seq_exame
and	b.nr_seq_padrao = NEW.nr_seq_padrao;

if (ie_existe_criterio_exame_w is not null) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(312048);
end if;

RETURN NEW;
end
