DROP TRIGGER IF EXISTS laudo_paciente_copia_insert ON laudo_paciente_copia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_laudo_paciente_copia_insert() RETURNS trigger AS $BODY$
declare
nr_versao_p	bigint;

pragma autonomous_transaction;

BEGIN

select 	coalesce(max(nr_versao),0)
into STRICT	nr_versao_p
from	laudo_paciente_copia
where	nr_seq_laudo = NEW.nr_seq_laudo;

NEW.nr_versao:= nr_versao_p + 1;

commit;

RETURN NEW;
end
$BODY$
