SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS laudo_paciente_insert_titulo ON laudo_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_laudo_paciente_insert_titulo() RETURNS trigger AS $BODY$
declare
   pragma	autonomous_transaction;
   ds_titulo_laudo_w	laudo_paciente.ds_titulo_laudo%type;
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then

	if((NEW.nr_seq_proc is not null)
		and (NEW.ds_titulo_laudo is not null)
		and (obter_se_somente_numero(NEW.ds_titulo_laudo) = 'S')) then
		
		
		select	substr(obter_desc_prescr_proc_laudo(p.cd_procedimento, p.ie_origem_proced, p.nr_seq_proc_interno, p.ie_lado, a.nr_sequencia),1,1200)
		into STRICT	ds_titulo_laudo_w	
		from	procedimento_paciente a,
			prescr_procedimento p
		where	a.nr_sequencia	= NEW.nr_seq_proc
		and	a.nr_prescricao	= p.nr_prescricao 
		and  p.nr_sequencia  = a.nr_sequencia_prescricao;

	  NEW.ds_titulo_laudo := ds_titulo_laudo_w;
	
	  commit;
	
	end if;
	
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_laudo_paciente_insert_titulo() FROM PUBLIC;

CREATE TRIGGER laudo_paciente_insert_titulo
	BEFORE INSERT OR UPDATE ON laudo_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_laudo_paciente_insert_titulo();

