SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS laudo_pac_rec_insert ON laudo_paciente_recipient CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_laudo_pac_rec_insert() RETURNS trigger AS $BODY$
declare

main_recipient_w          laudo_paciente_recipient.nr_sequencia%type;

BEGIN

IF ( coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S' ) THEN

        if (TG_OP = 'INSERT' AND NEW.ie_main_recipient = 'S') then

                select max(nr_sequencia)
                into STRICT main_recipient_w
                from laudo_paciente_recipient
                where ie_main_recipient = 'S'
                        and ie_situacao != 'I'
                        and nr_seq_laudo = NEW.nr_seq_laudo
                        and nr_atendimento = NEW.nr_atendimento;

                if (main_recipient_w > 0) then
                        CALL wheb_mensagem_pck.exibir_mensagem_abort(1058558);
                end if;
        end if;

        if (TG_OP = 'UPDATE' AND NEW.ie_main_recipient = 'S' AND OLD.ie_main_recipient = 'N' AND NEW.ie_situacao != 'I') then

                select max(nr_sequencia)
                into STRICT main_recipient_w
                from laudo_paciente_recipient
                where ie_main_recipient = 'S'
                        and ie_situacao != 'I'
                        and nr_seq_laudo = NEW.nr_seq_laudo
                        and nr_atendimento = NEW.nr_atendimento
                        and nr_sequencia != OLD.nr_sequencia;

                if (main_recipient_w > 0) then
                        CALL wheb_mensagem_pck.exibir_mensagem_abort(1058558);
                end if;
        end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_laudo_pac_rec_insert() FROM PUBLIC;

CREATE TRIGGER laudo_pac_rec_insert
	BEFORE INSERT OR UPDATE ON laudo_paciente_recipient FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_laudo_pac_rec_insert();

