CREATE OR REPLACE FUNCTION trigger_fct_leito_hig_controle_update() RETURNS trigger AS $BODY$
declare

BEGIN


if (NEW.dt_fim is not null) and (OLD.dt_fim is null) and (coalesce(NEW.nr_seq_interno,0)	> 0) then
	update	unidade_atendimento
	set	ie_status_unidade 	= 'L',
		nm_usuario		= NEW.nm_usuario,
		dt_atualizacao		= LOCALTIMESTAMP
	where	nr_seq_interno 		= NEW.nr_seq_interno;
end if;

RETURN NEW;
end
