SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS lista_problema_pac_atual ON lista_problema_pac CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_lista_problema_pac_atual() RETURNS trigger AS $BODY$
declare

nr_seq_sintoma_w  	 problema_ciap.nr_sequencia%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger    = 'S')  then
if (coalesce(NEW.cd_ciap,0) <> coalesce(OLD.cd_ciap,0)) then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_sintoma_w
	from	problema_ciap
	where	cd_ciap	= NEW.cd_ciap;
	
	if (nr_seq_sintoma_w > 0) then
		NEW.nr_seq_sintoma := nr_seq_sintoma_w;
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(851899);
	end if;
end if;

if (coalesce(OLD.DT_INICIO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_INICIO) and (NEW.DT_INICIO is not null) then
NEW.ds_utc := obter_data_utc(NEW.DT_INICIO, 'HV');
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
NEW.ds_utc_atualizacao := obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

if (OLD.dt_inativacao is null and NEW.dt_inativacao is not null and NEW.cd_evolucao is not null) then

	delete from clinical_note_soap_data
	where cd_evolucao = NEW.cd_evolucao and IE_MED_REC_TYPE = 'PROBLEM' and IE_STAGE = 1  and IE_SOAP_TYPE = 'A' and NR_SEQ_MED_ITEM = NEW.nr_sequencia;

end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_lista_problema_pac_atual() FROM PUBLIC;

CREATE TRIGGER lista_problema_pac_atual
	BEFORE INSERT OR UPDATE ON lista_problema_pac FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_lista_problema_pac_atual();

