SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS log_cleaning_atual ON log_cleaning CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_log_cleaning_atual() RETURNS trigger AS $BODY$
declare

qt_month_w	smallint;

BEGIN

	select 	coalesce(max(qt_storage_month),0)
	into STRICT 	qt_month_w
	from 	log_establishment
	where	cd_estabelecimento = NEW.cd_estabelecimento;

	if (NEW.qt_month >= qt_month_w) then
		if (qt_month_w > 1) then
            CALL wheb_mensagem_pck.exibir_mensagem_abort(805159, 'QT_MONTH=' || qt_month_w);
        else
            CALL wheb_mensagem_pck.exibir_mensagem_abort(805163, 'QT_MONTH=' || qt_month_w);
        end if;
	end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_log_cleaning_atual() FROM PUBLIC;

CREATE TRIGGER log_cleaning_atual
	BEFORE INSERT OR UPDATE ON log_cleaning FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_log_cleaning_atual();

