SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS lote_contabil_atual ON lote_contabil CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_lote_contabil_atual() RETURNS trigger AS $BODY$
declare

qt_registro_w		bigint;
cd_empresa_w		ctb_mes_ref.cd_empresa%type;
dt_referencia_w		ctb_mes_ref.dt_referencia%type;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S') then
	if (coalesce(NEW.nr_seq_mes_ref, 0) <> 0) then
		select  a.cd_empresa,
			a.dt_referencia
		into STRICT	cd_empresa_w,
			dt_referencia_w
		from    ctb_mes_ref a
		where   a.nr_sequencia = NEW.nr_seq_mes_ref;

		if (obter_empresa_estab(NEW.cd_estabelecimento) <>  cd_empresa_w) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1076351);
		end if;

		if (to_char(NEW.dt_referencia, 'mm/yyyy') <> to_char(dt_referencia_w, 'mm/yyyy')) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1153801,'DT_INICIAL=' 	|| to_char(pkg_date_utils.start_of(dt_referencia_w, 'MONTH'),'dd/mm/yyyy') ||
									';DT_FINAL=' 	|| to_char(pkg_date_utils.end_of(dt_referencia_w, 'MONTH'),'dd/mm/yyyy'));
		end if;
	end if;

	if (TG_OP = 'UPDATE') then
		if (NEW.nr_seq_mes_ref is null) and (OLD.nr_seq_mes_ref is not null) then
			select	count(*)
			into STRICT	qt_registro_w
			from 	ctb_movimento
			where 	nr_lote_contabil = NEW.nr_lote_contabil;

			if (qt_registro_w > 0) then
				/* O lote possui movimento na contabilidade, reatualize a consulta da tela.*/


				CALL wheb_mensagem_pck.exibir_mensagem_abort(266446);
			end if;
		end if;
	end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_lote_contabil_atual() FROM PUBLIC;

CREATE TRIGGER lote_contabil_atual
	BEFORE INSERT OR UPDATE ON lote_contabil FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_lote_contabil_atual();

