SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS lote_ent_secretaria_afterup ON lote_ent_secretaria CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_lote_ent_secretaria_afterup() RETURNS trigger AS $BODY$
declare
nr_ultimo_lote_w	bigint;
 
BEGIN 
 
if (NEW.dt_liberacao is not null) and (OLD.dt_liberacao is null) and (NEW.ie_lote_interno <> 'S') then 
 
	select	coalesce(max(nr_ultimo_lote),0) 
	into STRICT	nr_ultimo_lote_w 
	from	lote_ent_inst_posto 
	where	nr_seq_instituicao = NEW.nr_seq_instituicao 
	and		nr_sequencia = NEW.nr_seq_posto;
	 
	if (NEW.nr_lote > 0) then 
	 
		update 	lote_ent_inst_posto 
		set	nr_ultimo_lote = NEW.nr_lote 
		where	nr_seq_instituicao = NEW.nr_seq_instituicao 
		and	nr_sequencia = NEW.nr_seq_posto;
	 
	end if;
 
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_lote_ent_secretaria_afterup() FROM PUBLIC;

CREATE TRIGGER lote_ent_secretaria_afterup
	AFTER UPDATE ON lote_ent_secretaria FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_lote_ent_secretaria_afterup();

