SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS man_ordem_servico_binsert ON man_ordem_servico CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_man_ordem_servico_binsert() RETURNS trigger AS $BODY$
DECLARE

nm_user_w			varchar(50);
ds_versao_w			varchar(50);

BEGIN

if (NEW.nr_seq_localizacao is not null) and
	((NEW.nr_versao_cliente_abertura is null) or (coalesce(NEW.nr_seq_localizacao,0) <> coalesce(OLD.nr_seq_localizacao,0))) then

	select	username
	into STRICT	nm_user_w
	from	user_users;

	if (nm_user_w = 'CORP') then
		select	max(b.cd_versao || '.' ||b.cd_build)
		into STRICT	ds_versao_w
		from	man_localizacao_versao a,
				versao_alteracao b
		where	a.nr_seq_localizacao	= NEW.nr_seq_localizacao
		and		a.nr_seq_versao_alteracao = b.nr_sequencia
		and		a.dt_atualizacao_versao	= (	SELECT	max(dt_atualizacao_versao)
											from	man_localizacao_versao x
											where	x.nr_sequencia = a.nr_sequencia);

		if (ds_versao_w is not null) then
			NEW.nr_versao_cliente_abertura	:= ds_versao_w;
		end if;
	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_man_ordem_servico_binsert() FROM PUBLIC;

CREATE TRIGGER man_ordem_servico_binsert
	BEFORE INSERT OR UPDATE ON man_ordem_servico FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_man_ordem_servico_binsert();

