DROP TRIGGER IF EXISTS man_ordem_serv_arq_delete ON man_ordem_serv_arq CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_man_ordem_serv_arq_delete() RETURNS trigger AS $BODY$
DECLARE
nm_user_w		varchar(50);
nm_usuario_w	usuario.nm_usuario%type;
qt_usuario_w	bigint;

BEGIN

select username
into STRICT nm_user_w
from user_users;

nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

if (nm_user_w = 'CORP') and
	( ((coalesce(nm_usuario_w,'XPTO') <> coalesce(OLD.nm_usuario_nrec,'XPTO')) and (OLD.nm_usuario_nrec is not null)) and coalesce(OLD.ie_deletar, 'N') = 'N' ) then
	BEGIN

	select	count(1)
	into STRICT	qt_usuario_w
	from	usuario
	where	nm_usuario = nm_usuario_w
	and		ie_situacao = 'A';
		
	if (qt_usuario_w > 0) then
		/*Não é possível excluir o registro, usuário diferente do original.*/


		CALL wheb_mensagem_pck.exibir_mensagem_abort(363641);
	end if;
	
	end;
end if;

RETURN OLD;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
