SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS man_regra_orcamento_insert_upd ON man_regra_orcamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_man_regra_orcamento_insert_upd() RETURNS trigger AS $BODY$
declare

qt_regra_bloqueio_w	bigint;

BEGIN
if (NEW.ie_momento_regra = 'E') then
	select	count(*)
	into STRICT	qt_regra_bloqueio_w
	from	man_regra_orcamento_bloq
	where	nr_seq_orcamento = NEW.nr_sequencia
	and		ie_regra_bloqueio <> 'A';

	if (qt_regra_bloqueio_w > 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(457159);
		/*Quando utilizar a opção "Ao acesar a OS" no campo "Momento regra", o campo "Regra de bloqueio", deverá ter a opção "Avisar" selecionada. Favor ajustar.*/

	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_man_regra_orcamento_insert_upd() FROM PUBLIC;

CREATE TRIGGER man_regra_orcamento_insert_upd
	BEFORE INSERT OR UPDATE ON man_regra_orcamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_man_regra_orcamento_insert_upd();

