DROP TRIGGER IF EXISTS man_regra_orcam_bloq_ins_upd ON man_regra_orcamento_bloq CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_man_regra_orcam_bloq_ins_upd() RETURNS trigger AS $BODY$
declare

ie_momento_regra_w	varchar(1);

BEGIN
if (NEW.IE_REGRA_BLOQUEIO <> 'A') then
	select	max(IE_MOMENTO_REGRA)
	into STRICT	ie_momento_regra_w
	from	man_regra_orcamento
	where	nr_sequencia = NEW.nr_seq_orcamento;

	if (ie_momento_regra_w = 'E') then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(457159);
		/*Quando utilizar a opção "Ao acesar a OS" no campo "Momento regra", o campo "Regra de bloqueio", deverá ter a opção "Avisar" selecionada. Favor ajustar.*/

	end if;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_man_regra_orcam_bloq_ins_upd() FROM PUBLIC;

CREATE TRIGGER man_regra_orcam_bloq_ins_upd
	BEFORE INSERT OR UPDATE ON man_regra_orcamento_bloq FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_man_regra_orcam_bloq_ins_upd();

