SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS marca_atual ON marca CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_marca_atual() RETURNS trigger AS $BODY$
DECLARE
PRAGMA AUTONOMOUS_TRANSACTION;
ie_bloqueia_marca_igual_w		varchar(1);
qt_existe_w			bigint;
cd_estabelecimento_w		integer;
 
BEGIN 
 
select	wheb_usuario_pck.get_Cd_estabelecimento 
into STRICT	cd_estabelecimento_w
;
 
select	coalesce(max(IE_CAD_MARCA_IGUAL),'N') 
into STRICT	ie_bloqueia_marca_igual_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
if (ie_bloqueia_marca_igual_w = 'S') then 
	BEGIN 
 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	marca 
	where	nr_sequencia <> NEW.nr_sequencia 
	and	upper(elimina_acentuacao(ds_marca)) = upper(elimina_acentuacao(NEW.ds_marca));
 
	if (qt_existe_w > 0) then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266244);
		--'Já existe marca cadastrada com esta descrição!' || chr(10) || 
		--'Verifique.'); 
	end if;	
 
	end;
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_marca_atual() FROM PUBLIC;

CREATE TRIGGER marca_atual
	BEFORE INSERT OR UPDATE ON marca FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_marca_atual();

