SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS material_autor_cir_beforinsert ON material_autor_cirurgia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_autor_cir_beforinsert() RETURNS trigger AS $BODY$
declare

cd_convenio_w		bigint	:= null;
cd_estabelecimento_w	bigint;
cd_grupo_w		varchar(20)	:= '';
cd_mat_convenio_w	varchar(20)	:= '';
ds_mat_convenio_w	varchar(255)	:= '';
ie_tipo_atendimento_w	bigint;
nr_atendimento_w	bigint;
nr_atendimento_autor_w	bigint;
nr_seq_agenda_w		autorizacao_cirurgia.nr_seq_agenda%type;
nr_seq_autor_conv_w	bigint;
nr_seq_conversao_w	bigint;
cd_empresa_ref_w	bigint;
ie_carater_inter_sus_w	varchar(2);
ie_clinica_w		atendimento_paciente.ie_clinica%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

select	max(a.nr_seq_autor_conv),
	max(a.nr_seq_agenda),
	max(a.nr_atendimento)
into STRICT	nr_seq_autor_conv_w,
	nr_seq_agenda_w,
	nr_atendimento_w
from	autorizacao_cirurgia a
where	a.nr_sequencia		= NEW.nr_seq_autorizacao;


if (nr_seq_autor_conv_w is not null) then

	select	max(a.cd_convenio),
		max(a.nr_atendimento),
		max((obter_dados_autor_convenio(a.nr_sequencia, 'E'))::numeric )
	into STRICT	cd_convenio_w,
		nr_atendimento_autor_w,
		cd_estabelecimento_w
	from	autorizacao_convenio a
	where	a.nr_sequencia		= nr_seq_autor_conv_w;
	
	if (nr_atendimento_autor_w is not null) then
		select	max(ie_tipo_atendimento)
		into STRICT	ie_tipo_atendimento_w
		from	atendimento_paciente
		where	nr_atendimento	= nr_atendimento_w;
	end if;
end if;

if (cd_convenio_w is null) and (nr_atendimento_w is not null) then
	
	select	max(b.cd_convenio),
		max(a.nr_atendimento),
		max(a.cd_estabelecimento),
		max(a.ie_tipo_atendimento),
		coalesce(max(b.cd_empresa),0),
		max(a.ie_carater_inter_sus),
		max(a.ie_clinica)
	into STRICT	cd_convenio_w,
		nr_atendimento_autor_w,
		cd_estabelecimento_w,
		ie_tipo_atendimento_w,
		cd_empresa_ref_w,
		ie_carater_inter_sus_w,
		ie_clinica_w
	from	atend_categoria_convenio b,
		atendimento_paciente a
	where	a.nr_atendimento	= b.nr_atendimento
	and	b.nr_seq_interno	= Obter_Atecaco_atendimento(a.nr_atendimento)
	and 	a.nr_atendimento	= nr_atendimento_w;

end if;
	
if (cd_convenio_w is null) and (nr_seq_agenda_w is not null) then

	select	max(a.cd_convenio),
		max(a.ie_tipo_atendimento),
		max(obter_estab_agenda(a.cd_agenda))
	into STRICT	cd_convenio_w,
		ie_tipo_atendimento_w,
		cd_estabelecimento_w
	from 	agenda_paciente a
	where	a.nr_sequencia		= nr_seq_agenda_w;
end if;


if (NEW.cd_material is not null) and (cd_convenio_w is not null) then

	SELECT * FROM converte_material_convenio(cd_convenio_w, NEW.cd_material, null, null, NEW.cd_cgc_fornec, cd_estabelecimento_w, LOCALTIMESTAMP, null, cd_mat_convenio_w, cd_grupo_w, nr_seq_conversao_w, ie_tipo_atendimento_w, NEW.ie_origem_preco, cd_empresa_ref_w, null, ie_carater_inter_sus_w, null, null, ie_clinica_w, null) INTO STRICT cd_mat_convenio_w, cd_grupo_w, nr_seq_conversao_w;
end if;

if (cd_mat_convenio_w is not null) then

	NEW.cd_material_convenio	:= cd_mat_convenio_w;

	select	max(obter_desc_mat_convenio(cd_mat_convenio_w, cd_convenio_w))
	into STRICT	ds_mat_convenio_w
	;

	NEW.ds_mat_convenio		:= ds_mat_convenio_w;
end if;

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_material_autor_cir_beforinsert() FROM PUBLIC;

CREATE TRIGGER material_autor_cir_beforinsert
	BEFORE INSERT ON material_autor_cirurgia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_material_autor_cir_beforinsert();

