SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS material_bras_afterinsert ON material_brasindice CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_bras_afterinsert() RETURNS trigger AS $BODY$
declare

ds_titulo_w		varchar(80);
ds_historico_w		varchar(4000);

BEGIN

ds_titulo_w 	:= wheb_mensagem_pck.get_texto(802954);

ds_historico_w	:= wheb_mensagem_pck.get_texto(803040,
				'ds_medicamento=' || substr(obter_descricao_padrao('BRASINDICE_MEDICAMENTO','DS_MEDICAMENTO',NEW.cd_medicamento),1,40) || ';' ||
				'ds_laboratorio=' || substr(obter_descricao_padrao('BRASINDICE_LABORATORIO','DS_LABORATORIO',NEW.cd_laboratorio),1,40) || ';' ||
				'ds_apresentacao=' || substr(obter_descricao_padrao('BRASINDICE_APRESENTACAO','DS_APRESENTACAO',NEW.cd_apresentacao),1,60) || ';' ||
				'qt_conversao=' || NEW.qt_conversao);


insert into material_historico(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_historico,
	ds_historico_curto,
	cd_material,
	ds_titulo,
	cd_estabelecimento,
	ie_tipo)
SELECT	nextval('material_historico_seq'),
	LOCALTIMESTAMP,
	NEW.nm_usuario,
	LOCALTIMESTAMP,
	NEW.nm_usuario,
	ds_historico_w,
	substr(ds_historico_w,1,4000),
	NEW.cd_material,
	ds_titulo_w,
	NEW.cd_estabelecimento,
	'S'
;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_material_bras_afterinsert() FROM PUBLIC;

CREATE TRIGGER material_bras_afterinsert
	AFTER INSERT ON material_brasindice FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_material_bras_afterinsert();

