DROP TRIGGER IF EXISTS material_estab_update ON material_estab CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_estab_update() RETURNS trigger AS $BODY$
declare
ie_possui_reg_w		integer;

BEGIN

select	count(*)
into STRICT	ie_possui_reg_w
from	material_tipo_local_est
where	cd_material = OLD.cd_material;

if (ie_possui_reg_w > 0) then
	NEW.ie_revisar	:= 'R';
	gerar_comunic_revisao_mat(OLD.cd_material, null, NEW.dt_atualizacao, OLD.cd_estabelecimento, NEW.nm_usuario );
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_material_estab_update() FROM PUBLIC;

CREATE TRIGGER material_estab_update
	BEFORE UPDATE ON material_estab FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_material_estab_update();

