DROP TRIGGER IF EXISTS material_fornec_tie ON material_fornec CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_fornec_tie() RETURNS trigger AS $BODY$
declare
  ds_retorno_integracao_w    text;
  json_w                     philips_json;
  json_data_w                text;
BEGIN
  if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
    if (wheb_usuario_pck.get_nm_usuario is not null) then
      json_w := philips_json();
      json_w.put('id', NEW.NR_SEQUENCIA);
      json_w.put('material', NEW.CD_MATERIAL);
      json_w.put('supplier', NEW.CD_CGC);
      json_w.put('consigned', NEW.IE_CONSIGNADO);
      json_w.put('measurementUnit', NEW.CD_UNID_MEDIDA);
      json_w.put('establishment', NEW.CD_ESTABELECIMENTO);
      json_w.put('conversion', NEW.QT_CONV_COMPRA_EST);
      json_w.put('lastUpdate', NEW.DT_ATUALIZACAO);
      json_w.put('lastUpdateBy', NEW.NM_USUARIO);
      json_w.put('resupply', NEW.IE_RESSUPRIMENTO);
      json_w.put('invoiceStatus', NEW.IE_NOTA_FISCAL);
      json_w.put('purchaseQuote', NEW.IE_COT_COMPRA);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('materialsupplier.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
