DROP TRIGGER IF EXISTS material_repasse_delete ON material_repasse CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_repasse_delete() RETURNS trigger AS $BODY$
declare

ie_status_w	varchar(255);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	if (OLD.nr_repasse_terceiro is not null) then

		select 	ie_status
		into STRICT 	ie_status_w
		from 	repasse_terceiro
		where 	nr_repasse_terceiro = OLD.nr_repasse_terceiro;

		if (ie_status_w = 'F') then
			/* Este procedimento ja esta vinculado a um repasse!
			Nao e possivel excluir este procedimento.
			Repasse: #@NR_REPASSE_TERCEIRO#@ */

			CALL wheb_mensagem_pck.exibir_mensagem_abort(1168929, 'NR_REPASSE_TERCEIRO=' || OLD.nr_repasse_terceiro);
		end if;
	end if;
	
end if;
RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_material_repasse_delete() FROM PUBLIC;

CREATE TRIGGER material_repasse_delete
	BEFORE DELETE ON material_repasse FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_material_repasse_delete();

