SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS material_simpro_update ON material_simpro CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_simpro_update() RETURNS trigger AS $BODY$
declare

qt_existe_regra_w		varchar(1);
ds_erro_w			varchar(255) := '0';

BEGIN

select	coalesce(max('S'),'N')
into STRICT	qt_existe_regra_w
from	conv_regra_vinc_mat;

if (qt_existe_regra_w = 'S') then

	ds_erro_w := obter_parametro_vinculo_mat(NEW.cd_material, NEW.nm_usuario, ds_erro_w, 'S', 2);

	if (ds_erro_w <> '0') then
		--R.aise_application_error(-20011,ds_erro_w);
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263411,'ds_erro_w='||ds_erro_w);
	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_material_simpro_update() FROM PUBLIC;

CREATE TRIGGER material_simpro_update
	BEFORE UPDATE ON material_simpro FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_material_simpro_update();

