SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS material_tie ON material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_material_tie() RETURNS trigger AS $BODY$
declare
  ds_retorno_integracao_w    text;
  json_w                     philips_json;
  json_data_w                text;
BEGIN
  if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
    if (wheb_usuario_pck.get_nm_usuario is not null) then
      json_w := philips_json();

      json_w.put('code', NEW.CD_MATERIAL);
      json_w.put('description', NEW.DS_MATERIAL);
      json_w.put('shortDescription', NEW.DS_REDUZIDA);
      json_w.put('registrationDate', NEW.DT_CADASTRAMENTO);
      json_w.put('lastUpdateBy', NEW.NM_USUARIO);
      json_w.put('lastUpdate', NEW.DT_ATUALIZACAO);
      json_w.put('status', NEW.IE_SITUACAO);
      json_w.put('externalCode', NEW.CD_SISTEMA_ANT);
      json_w.put('stockMaterial', NEW.CD_MATERIAL_ESTOQUE);
      json_w.put('genericMaterial', NEW.CD_MATERIAL_GENERICO);
      json_w.put('consigned', NEW.IE_CONSIGNADO);
      json_w.put('volume', NEW.IE_VOLUMOSO);
      json_w.put('needsPrescription', NEW.IE_RECEITA);
      json_w.put('type', NEW.IE_TIPO_MATERIAL);
      json_w.put('stockedMaterial', NEW.IE_MATERIAL_ESTOQUE);
      json_w.put('abcAnalysis', NEW.IE_CURVA_ABC);
      json_w.put('allowOrders', NEW.IE_PRESCRICAO);
      json_w.put('isStandardized', NEW.IE_PADRONIZADO);
      json_w.put('dilution', NEW.IE_DILUICAO);
      json_w.put('solution', NEW.IE_SOLUCAO);
      json_w.put('mixture', NEW.IE_MISTURA);
      json_w.put('shelteredFromLight', NEW.IE_ABRIGO_LUZ);
      json_w.put('weightKg', NEW.QT_PESO_KG);
      json_w.put('controlledHumidity', NEW.IE_UMIDADE_CONTROLADA);
      json_w.put('chargesPatient', NEW.IE_COBRA_PACIENTE);
      json_w.put('infusionPump', NEW.IE_BOMBA_INFUSAO);
      json_w.put('conversionAmountMg', NEW.QT_CONVERSAO_MG);
      json_w.put('patientUsageSettlesStock', NEW.IE_BAIXA_ESTOQ_PAC);
      json_w.put('recordNotePrescription', NEW.IE_GRAVAR_OBS_PRESCR);
      json_w.put('requiresRouteOfAdministration', NEW.IE_OBRIG_VIA_APLICACAO);
      json_w.put('applicationMethod', NEW.IE_VIA_APLICACAO);
      json_w.put('medicalControl', NEW.IE_CONTROLE_MEDICO);
      json_w.put('prescriptionGroup', NEW.NR_SEQ_GRUPO_REC);
      json_w.put('usageHoursQuantity', NEW.QT_HORAS_UTIL_PAC);
      json_w.put('usualDoseByTimeQuantity', NEW.QT_MAX_PRESCRICAO);
      json_w.put('convStockConsumptionQuantity', NEW.QT_CONV_ESTOQUE_CONSUMO);
      json_w.put('consumptionUnitMeasurement', NEW.CD_UNIDADE_MEDIDA_CONSUMO);
      json_w.put('stockUnitMeasurement', NEW.CD_UNIDADE_MEDIDA_ESTOQUE);
      json_w.put('requestUnitMeasurement', NEW.CD_UNIDADE_MEDIDA_SOLIC);
      json_w.put('wholeSettlement', NEW.IE_BAIXA_INTEIRA);
      json_w.put('materialClass', NEW.CD_CLASSE_MATERIAL);
      json_w.put('materialFamily', NEW.NR_SEQ_FAMILIA);
      json_w.put('materialLocation', NEW.NR_SEQ_LOCALIZACAO);
      json_w.put('materialKit', NEW.CD_KIT_MATERIAL);
      json_w.put('materialManufacturer', NEW.NR_SEQ_FABRIC);
      json_w.put('partNumber', NEW.CD_FABRICANTE);
      json_w.put('certificateValidity', NEW.DT_VALIDADE_CERTIFICADO_APROV);
      json_w.put('approvalCertificateNumber', NEW.NR_CERTIFICADO_APROVACAO);
      json_w.put('maximumStockQuantity', NEW.QT_ESTOQUE_MAXIMO);
      json_w.put('minimumStockQuantity', NEW.QT_ESTOQUE_MINIMO);
      json_w.put('requestPoint', NEW.QT_PONTO_PEDIDO);
      json_w.put('validityDaysQuantity', NEW.QT_DIAS_VALIDADE);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('addmaterial.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_material_tie() FROM PUBLIC;

CREATE TRIGGER material_tie
	AFTER INSERT ON material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_material_tie();

