BEGIN
  BEGIN

BEGIN

ie_permite_mat_especiais_w := obter_param_usuario(871, 333, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_permite_mat_especiais_w);

