SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS med_atendimento_insert ON med_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_med_atendimento_insert() RETURNS trigger AS $BODY$
DECLARE
	CD_PESSOA_SIST_ORIG_W		varchar(10);
	CD_MEDICO_W				varchar(10);
BEGIN

SELECT	CD_PESSOA_SIST_ORIG,
		CD_MEDICO
INTO STRICT		CD_PESSOA_SIST_ORIG_W,
		CD_MEDICO_W
FROM		MED_CLIENTE
WHERE		NR_SEQUENCIA		= NEW.NR_SEQ_CLIENTE;

IF (CD_PESSOA_SIST_ORIG_W IS NULL) THEN
	BEGIN
	SELECT	coalesce(MAX(coalesce(SOMENTE_NUMERO(CD_PESSOA_SIST_ORIG),0)),0) + 1
	INTO STRICT		CD_PESSOA_SIST_ORIG_W
	FROM		MED_CLIENTE
	WHERE		CD_MEDICO		= CD_MEDICO_W;

	UPDATE	MED_CLIENTE
	SET		CD_PESSOA_SIST_ORIG	= CD_PESSOA_SIST_ORIG_W
	WHERE		NR_SEQUENCIA		= NEW.NR_SEQ_CLIENTE;

	END;
END IF;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_med_atendimento_insert() FROM PUBLIC;

CREATE TRIGGER med_atendimento_insert
	AFTER INSERT ON med_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_med_atendimento_insert();

