CREATE OR REPLACE FUNCTION trigger_fct_med_avaliacao_paciente_delete() RETURNS trigger AS $BODY$
declare

ie_possib_exluir_lib_w   varchar(1);
cd_estabelecimento_w     bigint          := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w             varchar(255)   := wheb_usuario_pck.get_nm_usuario;
qt_reg_w		smallint;

BEGIN
IF (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  THEN
	GOTO Final;
END IF;

ie_possib_exluir_lib_w := obter_param_usuario(944, 5, obter_perfil_ativo, nm_usuario_w, cd_estabelecimento_w, ie_possib_exluir_lib_w);

if (ie_possib_exluir_lib_w <> 'S') and (OLD.dt_liberacao	is not null) then
	--Não é possível excluir uma avaliação já liberada.
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(191755);
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN OLD;
end
