SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS med_receita_pend_atual ON med_receita CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_med_receita_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_lib_receita_w	varchar(10);
BEGIN
  BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;
BEGIN

select	max(IE_LIB_RECEITA)
into STRICT	ie_lib_receita_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (coalesce(ie_lib_receita_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'REC';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XREC';
	end if;
	if (ie_tipo_w	is not null) then
    CALL Gerar_registro_pendente_PEP( cd_tipo_registro_p => ie_tipo_w,
      nr_sequencia_registro_p => NEW.nr_sequencia,
      cd_pessoa_fisica_p => coalesce(NEW.cd_pessoa_fisica,substr(obter_pessoa_atendimento(NEW.nr_atendimento_hosp,'C'),1,255)),
      nr_atendimento_p => NEW.nr_atendimento_hosp,
      nm_usuario_p => NEW.nm_usuario,
      nr_atend_cons_pepa_p => NEW.nr_seq_atend_cons_pepa);
	end if;
end if;


exception
when others then
	null;
end;
	
<<Final>>
qt_reg_w	:= 0;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_med_receita_pend_atual() FROM PUBLIC;

CREATE TRIGGER med_receita_pend_atual
	AFTER INSERT OR UPDATE ON med_receita FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_med_receita_pend_atual();

