CREATE OR REPLACE FUNCTION trigger_fct_movto_alt_delete_tit_rec() RETURNS trigger AS $BODY$
declare
count_w	bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
select	count(*)
into STRICT	count_w
from	alteracao_valor a,
	titulo_receber b
where	cd_motivo	= OLD.nr_sequencia
and	a.nr_titulo	= b.nr_titulo;


if (count_w > 0 ) then

	CALL wheb_mensagem_pck.exibir_mensagem_abort(373008);
	
end if;
end if;

RETURN OLD;
end;
