DROP TRIGGER IF EXISTS movto_banco_pend_baixa_atual ON movto_banco_pend_baixa CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_movto_banco_pend_baixa_atual() RETURNS trigger AS $BODY$
declare
cd_estabelecimento_w    ctb_documento.cd_estabelecimento%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	movto_banco_pend
	where	nr_sequencia = NEW.nr_seq_movto_pend;
	
	/* Projeto Davita  - Nao deixa gerar movimento contabil apos fechamento  da data */


	CALL philips_contabil_pck.valida_se_dia_fechado(OBTER_EMPRESA_ESTAB(cd_estabelecimento_w),NEW.dt_baixa);
end if;	
	
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
