SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS movto_cartao_cr_delete ON movto_cartao_cr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_movto_cartao_cr_delete() RETURNS trigger AS $BODY$
DECLARE

dt_fechamento_w		timestamp	:= null;
nr_recibo_w		bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (OLD.nr_seq_caixa_rec is not null) then
	select	max(dt_fechamento),
		max(nr_recibo)
	into STRICT	dt_fechamento_w,
		nr_recibo_w
	from	caixa_receb
	where	nr_sequencia	= OLD.nr_seq_caixa_rec;

	if (dt_fechamento_w is not null) then
		/* Nao e possivel excluir esse cartao!
		O recibo #@NR_RECIBO#@ ja foi fechado! */

		CALL wheb_mensagem_pck.exibir_mensagem_abort(267091, 'NR_RECIBO=' || nr_recibo_w);
	end if;
end if;
end if;

RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_movto_cartao_cr_delete() FROM PUBLIC;

CREATE TRIGGER movto_cartao_cr_delete
	BEFORE DELETE ON movto_cartao_cr FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_movto_cartao_cr_delete();

