SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS movto_cartao_cr_insert ON movto_cartao_cr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_movto_cartao_cr_insert() RETURNS trigger AS $BODY$
DECLARE

dt_fechamento_w		timestamp	:= null;
nr_recibo_w		bigint;
nr_seq_produto_w	bigint;
nr_seq_grupo_w		bigint;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S')	= 'S')  then

if (NEW.nr_seq_caixa_rec is not null) then
	select	max(dt_fechamento),
		max(nr_recibo)
	into STRICT	dt_fechamento_w,
		nr_recibo_w
	from	caixa_receb
	where	nr_sequencia	= NEW.nr_seq_caixa_rec;

	if (dt_fechamento_w is not null) then
		/*r.aise_application_error(-20011,'Nao e possivel inserir um novo cartao!' || chr(13) ||
					'O recibo ' || nr_recibo_w || ' ja foi fechado!');*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(215513,'NR_RECIBO_W='||nr_recibo_w);
	end if;
end if;

SELECT * FROM obter_produto_financeiro(NEW.cd_estabelecimento, null, NEW.cd_cgc, nr_seq_produto_w, null, null, nr_seq_grupo_w) INTO STRICT nr_seq_produto_w, nr_seq_grupo_w;

/* Projeto Davita  - Nao deixa gerar movimento contabil apos fechamento  da data */


CALL philips_contabil_pck.valida_se_dia_fechado(OBTER_EMPRESA_ESTAB(NEW.cd_estabelecimento),NEW.dt_transacao);


if (nr_seq_grupo_w is not null) then
	NEW.nr_seq_grupo_prod	:= nr_seq_grupo_w;
end if;

NEW.DS_STACK := substr('CallStack Insert: ' || substr(dbms_utility.format_call_stack,1,3980),1,4000);

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_movto_cartao_cr_insert() FROM PUBLIC;

CREATE TRIGGER movto_cartao_cr_insert
	BEFORE INSERT ON movto_cartao_cr FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_movto_cartao_cr_insert();

