SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS mprev_agendamento_bef_atual ON mprev_agendamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_mprev_agendamento_bef_atual() RETURNS trigger AS $BODY$
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	NEW.dt_final_agenda	:= NEW.dt_agenda + (NEW.nr_minuto_duracao * (1/24/60));

	/* Não pode haver agendamento sem participante ou turma */

	if	((NEW.nr_seq_participante is null) and (NEW.nr_seq_turma is null) and (NEW.nr_seq_captacao is null) and (NEW.nr_seq_ativ_extra is null)) then
		--O agendamento precisa ser para um participante ou turma.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(270306);
	end if;

	/* Tratar vínculo do plano de atendimento com o agendamento */

	/*if	(:new.nr_seq_partic_ciclo_item is null) then  OS 855230 */

		/* :new.nr_seq_partic_ciclo_item	:= mprev_obter_atend_prev_data(:new.nr_seq_participante,:new.dt_agenda); OS 855230 */

		/* Se achou o plano, atualizar no atendimento previsto do ciclo o status */

		if	(OLD.nr_seq_partic_ciclo_item is null AND NEW.nr_seq_partic_ciclo_item is not null) or
			((OLD.nr_seq_partic_ciclo_item is not null) and (OLD.nr_seq_partic_ciclo_item  <> NEW.nr_seq_partic_ciclo_item) and (NEW.nr_seq_partic_ciclo_item is not null)) then
			update	mprev_partic_ciclo_item
			set	ie_status	= 'A', /* Agendado */
				dt_atualizacao	= LOCALTIMESTAMP,
				nm_usuario	= NEW.nm_usuario
			where	nr_sequencia	= NEW.nr_seq_partic_ciclo_item
			and	ie_status	= 'P';
		end if;
	/*end if;  OS 855230 */

end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_mprev_agendamento_bef_atual() FROM PUBLIC;

CREATE TRIGGER mprev_agendamento_bef_atual
	BEFORE INSERT OR UPDATE ON mprev_agendamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_mprev_agendamento_bef_atual();

