DROP TRIGGER IF EXISTS mprev_captacao_insert ON mprev_captacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_mprev_captacao_insert() RETURNS trigger AS $BODY$
declare

qt_registro_w	integer;

BEGIN

select	count(1)
into STRICT	qt_registro_w
from	mprev_participante a
where	a.cd_pessoa_fisica = NEW.cd_pessoa_fisica;

if (qt_registro_w = 0) then
	insert into mprev_participante(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_pessoa_fisica,
		ie_status,
		ie_situacao,
		ie_internacao,
		ie_cronico)
	values (nextval('mprev_participante_seq'),
		NEW.nm_usuario,
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		LOCALTIMESTAMP,
		NEW.cd_pessoa_fisica,
		'1', /* Captação */
		'A', /* Ativo*/
		'N', /* Não internado */
		'N' /* Não crônico */
);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
