SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS mprev_programa_partic_atual ON mprev_programa_partic CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_mprev_programa_partic_atual() RETURNS trigger AS $BODY$
DECLARE

nr_atendimento_baixa_w	atendimento_paciente.nr_atendimento%type;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') <> 'N') then

	if (trunc(NEW.dt_exclusao) < trunc(NEW.dt_inclusao)) then
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(834574);
	end if;

	if (obter_utiliza_nom(wheb_usuario_pck.get_cd_estabelecimento) = 'S') then
		if (OLD.dt_exclusao is null and NEW.dt_exclusao is not null) and (NEW.ie_motivo_baixa is not null) then
			select	max(nr_atendimento)
			into STRICT	nr_atendimento_baixa_w
			from	atendimento_paciente b,
					mprev_participante a
			where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
			and		a.nr_sequencia = NEW.nr_seq_participante;
			
			if (nr_atendimento_baixa_w is not null) then
				NEW.nr_atendimento_baixa := nr_atendimento_baixa_w;
			end if;
		elsif (OLD.dt_exclusao is not null and NEW.dt_exclusao is null) and (NEW.nr_atendimento_baixa is not null) then
			NEW.nr_atendimento_baixa	:= null;
		end if;
	end if;
end if;
	
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_mprev_programa_partic_atual() FROM PUBLIC;

CREATE TRIGGER mprev_programa_partic_atual
	BEFORE INSERT OR UPDATE ON mprev_programa_partic FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_mprev_programa_partic_atual();

