DROP TRIGGER IF EXISTS mprev_programa_partic_insert ON mprev_programa_partic CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_mprev_programa_partic_insert() RETURNS trigger AS $BODY$
declare

nm_programa_w        mprev_programa.nm_programa%type;
cd_pessoa_fisica_w   pessoa_fisica.cd_pessoa_fisica%type;
nr_seq_envio_mens_w  mprev_regra_cubo.nr_seq_envio_mens%type;

cRegras CURSOR FOR
SELECT nr_sequencia
from   mprev_regra_envio_mensagem
where  ie_momento_envio = 'IP';

BEGIN
    if (wheb_usuario_pck.get_ie_executar_trigger = 'S' ) then
      if (wheb_usuario_pck.get_cd_funcao = '10157') then
          select cd_pessoa_fisica
          into STRICT   cd_pessoa_fisica_w
          from   mprev_participante
          where  nr_sequencia = NEW.nr_seq_participante;

          select nm_programa
          into STRICT   nm_programa_w
          from   mprev_programa
          where  nr_sequencia = NEW.nr_seq_programa;

          open cRegras;
          loop
          fetch cRegras into nr_seq_envio_mens_w;
          EXIT WHEN NOT FOUND; /* apply on cRegras */
              BEGIN
                  CALL mprev_enviar_email_sms(cd_pessoa_fisica_w , nr_seq_envio_mens_w, nm_programa_w, '10157', NEW.nm_usuario);
              end;
          end loop;
          close cRegras;
      end if;
    end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_mprev_programa_partic_insert() FROM PUBLIC;

CREATE TRIGGER mprev_programa_partic_insert
	AFTER INSERT ON mprev_programa_partic FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_mprev_programa_partic_insert();

