CREATE OR REPLACE FUNCTION trigger_fct_natureza_gasto_atual() RETURNS trigger AS $BODY$
declare

cd_grupo_natureza_gasto_w	bigint;
BEGIN

if (coalesce(NEW.nr_seq_gng,0) <> 0) and (NEW.cd_estabelecimento is null) then

	select	max(cd_estabelecimento)
	into STRICT	NEW.cd_estabelecimento
	from	grupo_natureza_gasto
	where	nr_sequencia	= NEW.nr_seq_gng;

end if;

RETURN NEW;
end
