SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS nota_fiscal_delete ON nota_fiscal CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nota_fiscal_delete() RETURNS trigger AS $BODY$
DECLARE

qt_existe_w	bigint;

BEGIN

if (OLD.dt_atualizacao_estoque is not null) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266256);
	--'Esta nota já foi calculada, Não pode mais ser modificada!');
end if;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_registro
where	nr_seq_nf = OLD.nr_sequencia
and	coalesce(ie_origem,'X') = 'NF';

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(324801);
else
	select	count(*)
	into STRICT	qt_existe_w
	from	inspecao_registro
	where	nr_seq_nf = OLD.nr_sequencia;

	if (qt_existe_w > 0) then
		update	inspecao_registro
		set	nr_seq_nf  = NULL
		where	nr_seq_nf = OLD.nr_sequencia;
	end if;
end if;

select	count(*)
into STRICT	qt_existe_w
from	inspecao_recebimento
where	nr_seq_nota_fiscal = OLD.nr_sequencia;

if (qt_existe_w > 0) then
	update	inspecao_recebimento
	set	nr_seq_nota_fiscal  = NULL,
		nr_seq_item_nf	    = NULL
	where	nr_seq_nota_fiscal = OLD.nr_sequencia;
end if;

if (OLD.nr_seq_lote_prot is not null) then
	update	lote_protocolo
	set	dt_geracao_nota	 = NULL
	where	nr_sequencia 	= OLD.nr_seq_lote_prot;
end if;

-- validações da função OPS - Pagamento de Produção Médica
select	count(1)
into STRICT	qt_existe_w
from	pls_pp_base_acum_trib
where	nr_seq_nota_fiscal = OLD.nr_sequencia;

if (qt_existe_w > 0) then
	-- Não é possível excluir esta nota, seus tributos foram utilizados como base de cálculo na função
	-- OPS - Pagamentos de Produção Médica(nova).
	CALL wheb_mensagem_pck.exibir_mensagem_abort(461022);
end if;

select	count(1)
into STRICT	qt_existe_w
from	pls_pp_lr_base_trib
where	nr_seq_nota_fiscal = OLD.nr_sequencia;

if (qt_existe_w > 0) then
	-- Não é possível excluir esta nota, seus tributos foram utilizados como base de cálculo na função
	-- OPS - Pagamentos de Produção Médica(nova).
	CALL wheb_mensagem_pck.exibir_mensagem_abort(461022);
end if;
-- fim validações da função OPS - Pagamento de Produção Médica
CALL desvincular_nf_venc_contrato(OLD.nr_sequencia);

RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_nota_fiscal_delete() FROM PUBLIC;

CREATE TRIGGER nota_fiscal_delete
	BEFORE DELETE ON nota_fiscal FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_nota_fiscal_delete();

