DROP TRIGGER IF EXISTS nota_fiscal_item_afterinsert ON nota_fiscal_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nota_fiscal_item_afterinsert() RETURNS trigger AS $BODY$
declare
 
cd_estabelecimento_w		smallint;
dt_atualizacao_estoque_w		timestamp;
ie_variacao_fiscal_nf_w		varchar(1);
qt_existe_w			bigint;
 
BEGIN 
 
select	cd_estabelecimento, 
	dt_atualizacao_estoque 
into STRICT	cd_estabelecimento_w, 
	dt_atualizacao_estoque_w 
from	nota_fiscal 
where	nr_sequencia = NEW.nr_sequencia;
 
select	count(*) 
into STRICT	qt_existe_w 
from	fis_variacao_fiscal 
where	ie_situacao = 'A';
 
select	coalesce(max(ie_variacao_fiscal_nf),'N') 
into STRICT	ie_variacao_fiscal_nf_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
if (dt_atualizacao_estoque_w is null) and (ie_variacao_fiscal_nf_w = 'S') and (qt_existe_w > 0) then 
	 
	CALL gerar_tributos_item_nf(	NEW.nr_sequencia, NEW.nr_item_nf, NEW.nr_seq_variacao_fiscal, NEW.vl_liquido, NEW.nm_usuario);
	 
end if;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
