DROP TRIGGER IF EXISTS nota_fiscal_item_delete ON nota_fiscal_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nota_fiscal_item_delete() RETURNS trigger AS $BODY$
DECLARE

qt_existe_w		bigint;

BEGIN

if (coalesce(OLD.nr_seq_inspecao,0) > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	inspecao_recebimento
	where	nr_sequencia = coalesce(OLD.nr_seq_inspecao,0);

	if (qt_existe_w > 0) then
		update	inspecao_recebimento
		set	nr_seq_item_nf  = NULL
		where	nr_sequencia = OLD.nr_seq_inspecao;
	end if;
end if;

RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
