SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS nota_fiscal_item_insert ON nota_fiscal_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nota_fiscal_item_insert() RETURNS trigger AS $BODY$
declare

ie_pais_w	valor_dominio.vl_dominio%type;
cd_natureza_operacao_w	natureza_operacao.cd_natureza_operacao%type;

BEGIN
ie_pais_w	:=	coalesce(pkg_i18n.get_user_locale, 'pt_BR');

if (ie_pais_w = 'es_MX') or (ie_pais_w = 'es_DO') or (ie_pais_w = 'ja_JP') then
	BEGIN
	
	select	coalesce(max(cd_natureza_operacao), 0)
	into STRICT	cd_natureza_operacao_w
	from	nota_fiscal
	where	nr_sequencia = NEW.nr_sequencia;
	
	if (cd_natureza_operacao_w <>  NEW.cd_natureza_operacao) then
			NEW.cd_natureza_operacao := cd_natureza_operacao_w;
	end if;

	if (cd_natureza_operacao_w = 0) and (NEW.cd_natureza_operacao = 0) and
		((ie_pais_w = 'es_DO') OR (ie_pais_w = 'ja_JP')) then
		BEGIN
			NEW.cd_natureza_operacao := null;
		end;
		end if;
	end;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_nota_fiscal_item_insert() FROM PUBLIC;

CREATE TRIGGER nota_fiscal_item_insert
	BEFORE INSERT OR UPDATE ON nota_fiscal_item FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_nota_fiscal_item_insert();

