SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS nota_fiscal_lote_nfe_update ON nota_fiscal_lote_nfe CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nota_fiscal_lote_nfe_update() RETURNS trigger AS $BODY$
declare

ds_param_integ_hl7_w	varchar(255);
qt_registro_w		bigint;
nr_seq_transmissao_w	nfe_transmissao.nr_sequencia%type;
nr_seq_lote_w		nota_fiscal_lote_nfe.nr_sequencia%type;
qt_espera_consulta_w	w_nfse_envio_automatico.qt_espera_consulta%type := 0;
ie_envio_aut_nfse_w	parametro_compras.ie_envio_aut_nfse%type := 'N';
	
BEGIN

if (TG_OP = 'UPDATE') and (NEW.nr_protocolo is not null) and (OLD.nr_protocolo is null) then
	BEGIN

	select	count(1)
	into STRICT	qt_registro_w
	from	parametro_compras
	where	cd_estabelecimento = NEW.cd_estabelecimento;
	
	if (qt_registro_w > 0) then
		BEGIN
	
		select	coalesce(max(ie_envio_aut_nfse),'N')
		into STRICT	ie_envio_aut_nfse_w
		from	parametro_compras
		where	cd_estabelecimento = NEW.cd_estabelecimento;
		
		end;
	end if;
	

	if (ie_envio_aut_nfse_w = 'S') then
		BEGIN
		
		select	count(1)
		into STRICT	qt_registro_w
		from	w_nfse_envio_automatico
		where	nr_seq_transmissao = NEW.nr_seq_transmissao;
		
		if (qt_registro_w > 0) then
			BEGIN
			
			select	coalesce(max(qt_espera_consulta),0)
			into STRICT	qt_espera_consulta_w
			from	w_nfse_envio_automatico
			where	nr_seq_transmissao = NEW.nr_seq_transmissao;
		
			end;
		end if;
		
		nr_seq_transmissao_w := gerar_transmissao_nf(NEW.nm_usuario, 3, 'NFSE', nr_seq_transmissao_w);
		
		CALL gravar_nf_transmissao_consulta(nr_seq_transmissao_w,NEW.nr_seq_transmissao,NEW.nm_usuario);

		select	max(nr_sequencia)
		into STRICT	nr_seq_lote_w
		from	nota_fiscal_lote_nfe
		where	nr_seq_transmissao = NEW.nr_seq_transmissao;

			
		ds_param_integ_hl7_w := 'NR_SEQ_TRANSMISSAO=' || nr_seq_transmissao_w || ';CD_ESTABELECIMENTO='|| NEW.cd_estabelecimento ||';NM_USUARIO='|| NEW.nm_usuario || ';LOTE_NFSE=' || nr_seq_lote_w || ';CD_PERFIL=' || obter_perfil_ativo || ';'
					|| 'QT_ESPERA_CONSULTA=' || qt_espera_consulta_w || ';';
		CALL gravar_agend_integracao(532, ds_param_integ_hl7_w);
		
		end;
	end if;
		

	end;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_nota_fiscal_lote_nfe_update() FROM PUBLIC;

CREATE TRIGGER nota_fiscal_lote_nfe_update
	BEFORE UPDATE ON nota_fiscal_lote_nfe FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_nota_fiscal_lote_nfe_update();

