DROP TRIGGER IF EXISTS nut_horario_update_html ON nut_servico_horario CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nut_horario_update_html() RETURNS trigger AS $BODY$
DECLARE

cd_estabelecimento_w 			estabelecimento.cd_estabelecimento%type;
nm_usuario_w 					usuario.nm_usuario%type;
ie_html_w						varchar(1);

BEGIN
ie_html_w := 'N';

/*rotina específica para a plataforma HTML5 */


/* campos ds_horarios date (para utilização do dateTimePickerr) em HTML x ds_horarios String em Java*/


if ((NEW.DS_HORARIOS_HTML is not null and (NEW.DS_HORARIOS_HTML <> OLD.DS_HORARIOS_HTML or OLD.DS_HORARIOS_HTML is null)) or (NEW.DS_HORARIOS_FIM_HTML is not null	and (NEW.DS_HORARIOS_FIM_HTML <> OLD.DS_HORARIOS_FIM_HTML or OLD.DS_HORARIOS_FIM_HTML is null)) or (NEW.DS_HORARIO_CORTE_INIC_HTML is not null and (NEW.DS_HORARIO_CORTE_INIC_HTML <> OLD.DS_HORARIO_CORTE_INIC_HTML or OLD.DS_HORARIO_CORTE_INIC_HTML is null)) or (NEW.DS_HORARIO_CORTE_FIM_HTML is not null and (NEW.DS_HORARIO_CORTE_FIM_HTML <> OLD.DS_HORARIO_CORTE_FIM_HTML or OLD.DS_HORARIO_CORTE_FIM_HTML is null))) then
	BEGIN
	ie_html_w := 'S';
	cd_estabelecimento_w 	:= wheb_usuario_pck.get_cd_estabelecimento;
	nm_usuario_w 			:= wheb_usuario_pck.get_nm_usuario;
	
	NEW.DS_HORARIOS 			:= to_char(NEW.ds_horarios_html, 'HH24:MI');
	NEW.DS_HORARIOS_FIM 		:= to_char(NEW.ds_horarios_fim_html, 'HH24:MI');
	NEW.DS_HORARIO_CORTE_INIC	:= to_char(NEW.ds_horario_corte_inic_html, 'HH24:MI');
	NEW.DS_HORARIO_CORTE_FIM 	:= to_char(NEW.ds_horario_corte_fim_html, 'HH24:MI');
	end;
end if;

if (ie_html_w = 'N' and (NEW.DS_HORARIOS is not null and (NEW.DS_HORARIOS <> OLD.DS_HORARIOS or OLD.DS_HORARIOS is null)) or (NEW.DS_HORARIOS_FIM is not null	and (NEW.DS_HORARIOS_FIM <> OLD.DS_HORARIOS_FIM or OLD.DS_HORARIOS_FIM is null)) or (NEW.DS_HORARIO_CORTE_INIC is not null and (NEW.DS_HORARIO_CORTE_INIC <> OLD.DS_HORARIO_CORTE_INIC or OLD.DS_HORARIO_CORTE_INIC is null)) or (NEW.DS_HORARIO_CORTE_FIM is not null and (NEW.DS_HORARIO_CORTE_FIM <> OLD.DS_HORARIO_CORTE_FIM or OLD.DS_HORARIO_CORTE_FIM is null))) then
	BEGIN
	cd_estabelecimento_w 	:= wheb_usuario_pck.get_cd_estabelecimento;
	nm_usuario_w 			:= wheb_usuario_pck.get_nm_usuario;
	
	NEW.DS_HORARIOS_HTML 			:= to_date(NEW.ds_horarios, 'HH24:MI');
	NEW.DS_HORARIOS_FIM_HTML 		:= to_date(NEW.ds_horarios_fim, 'HH24:MI');
	NEW.DS_HORARIO_CORTE_INIC_HTML	:= to_date(NEW.ds_horario_corte_inic, 'HH24:MI');
	NEW.DS_HORARIO_CORTE_FIM_HTML 	:= to_date(NEW.ds_horario_corte_fim, 'HH24:MI');
	end;
end if;

RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
