DROP TRIGGER IF EXISTS nut_reserva_pf_atual ON nut_reserva_pf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_nut_reserva_pf_atual() RETURNS trigger AS $BODY$
DECLARE

BEGIN
if (NEW.nm_usuario <> OLD.nm_usuario) or (NEW.nm_usuario <> OLD.nm_usuario_nrec) then
	insert into log_nutricao(
			dt_atualizacao,
			nm_usuario,
			cd_log,
			ds_log)
		values (LOCALTIMESTAMP,
			NEW.nm_usuario,
			77452,
			'Alteração realizada no dia ' || to_char(LOCALTIMESTAMP,'dd/mm/yyyy hh24:mi:ss') || ' pelo usuário ' || NEW.nm_usuario || ', reserva gerada pelo usuário ' || OLD.nm_usuario_nrec);
end if;

RETURN NEW;
end
