SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ocorrencia_atual ON ocorrencia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ocorrencia_atual() RETURNS trigger AS $BODY$
declare
cd_perfil_ativo	bigint := 0;
BEGIN
  BEGIN
	cd_perfil_ativo :=  obter_perfil_ativo;

	if (cd_perfil_ativo > 0) and (NEW.cd_perfil is null)then
		BEGIN
		NEW.CD_PERFIL	:=	cd_perfil_ativo;
		exception
		when others then
		null;
		end;
	end if;

if (NEW.NR_SEQ_TURNO is null) then
	NEW.nr_seq_turno := Obter_Turno_ocorrencia(NEW.DT_OCORRENCIA,NEW.cd_estabelecimento,'SEQ','EQ');
end if;

if (coalesce(OLD.DT_OCORRENCIA,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_OCORRENCIA) and (NEW.DT_OCORRENCIA is not null) then
	NEW.ds_utc		:= obter_data_utc(NEW.DT_OCORRENCIA, 'HV');
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ocorrencia_atual() FROM PUBLIC;

CREATE TRIGGER ocorrencia_atual
	BEFORE INSERT OR UPDATE ON ocorrencia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ocorrencia_atual();

