CREATE OR REPLACE FUNCTION trigger_fct_oft_consulta_delete() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN
  BEGIN
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

update   atendimento_paciente
set      nr_seq_oftalmo  = NULL 
where    nr_seq_oftalmo = OLD.nr_sequencia;

update   agenda_paciente
set      nr_seq_oftalmo  = NULL
where    nr_seq_oftalmo = OLD.nr_sequencia;

update   agenda_consulta
set      nr_seq_oftalmo  = NULL
where    nr_seq_oftalmo = OLD.nr_sequencia;

update   atestado_paciente
set      nr_seq_consulta  = NULL
where    nr_seq_consulta = OLD.nr_sequencia;

update   prescr_medica
set      nr_seq_consulta_oft  = NULL
where    nr_seq_consulta_oft = OLD.nr_sequencia;

end if;

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN OLD;
END;
$BODY$
