DROP TRIGGER IF EXISTS oft_consulta_medica_pend_atual ON oft_consulta_medica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_oft_consulta_medica_pend_atual() RETURNS trigger AS $BODY$
declare
 
qt_reg_w		smallint;
ie_tipo_w		varchar(10);
IE_LIB_OFTALMOLOGIA_w	varchar(10);
nr_seq_reg_elemento_w	bigint;
BEGIN
  BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
select	max(IE_LIB_OFTALMOLOGIA) 
into STRICT	IE_LIB_OFTALMOLOGIA_w 
from	parametro_medico 
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
 
BEGIN 
 
	if (TG_OP = 'INSERT') or (TG_OP = 'UPDATE') then 
 
		if (coalesce(IE_LIB_OFTALMOLOGIA_w,'N') = 'S') then 
			if (NEW.dt_liberacao is null) then 
				ie_tipo_w := 'OFT';
			elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then 
				ie_tipo_w := 'XOFT';
			end if;
 
 
			if (ie_tipo_w	is not null) then 
				CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario,'L',null,null,null,null,null,null,null,null,null,null,null,null,null,null,nr_seq_reg_elemento_w);
			end if;
 
			 
		end if;
	elsif (TG_OP = 'DELETE') then 
		ie_tipo_w := 'XOFT';
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, OLD.nr_sequencia, obter_pessoa_atendimento(OLD.nr_atendimento,'C'), OLD.nr_atendimento, OLD.nm_usuario,'L',null,null,null,null,null,null,null,null,null,null,null,null,null,null,nr_seq_reg_elemento_w);
	end if;
 
exception 
when others then 
	null;
end;
 
<<Final>> 
qt_reg_w	:= 0;
  END;
IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
