SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS oft_consulta_update ON oft_consulta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_oft_consulta_update() RETURNS trigger AS $BODY$
declare

ie_modifica_fim_cons_w	varchar(1);
nr_sequencia_w 	bigint;
ie_liberar_avaliacao_w varchar(1);
ie_atualiza_triagem_w   varchar(1);
cd_perfil_w          perfil.cd_perfil%type                     := wheb_usuario_pck.get_cd_perfil;
nm_usuario_w         usuario.nm_usuario%type                   := wheb_usuario_pck.get_nm_usuario;
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type   := wheb_usuario_pck.get_cd_estabelecimento;
BEGIN
  BEGIN
ie_atualiza_triagem_w := obter_param_usuario(3010, 150, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_atualiza_triagem_w);

BEGIN
if (ie_atualiza_triagem_w = 'S') and (OLD.nr_atendimento is null) and (NEW.nr_atendimento is not null) then
   update   atendimento_paciente
   set      dt_inicio_atendimento   = NEW.dt_consulta,
            dt_fim_triagem          = NEW.dt_consulta
   where    nr_atendimento          = NEW.nr_atendimento
   and      dt_inicio_atendimento   is null
   and      dt_fim_triagem          is null;
end if;
exception
when others then
	null;
end;


if ((coalesce(OLD.nr_seq_status,0)) <> coalesce(NEW.nr_seq_status,0)) then
	select	max(IE_LIBERAR_AVALIACAO)
	into STRICT	ie_liberar_avaliacao_w
	from	oft_status_consulta 
	where 	nr_sequencia = NEW.nr_seq_status;
	
	if (ie_liberar_avaliacao_w = 'S') then
		update med_avaliacao_paciente
		set    DT_LIBERACAO = LOCALTIMESTAMP
		where  nr_atendimento = NEW.nr_atendimento
		and    DT_LIBERACAO is null
		and    nr_seq_consulta = NEW.nr_sequencia;
	end if;
end if;
	
if (NEW.nr_seq_status is not null) and
	((coalesce(OLD.nr_seq_status,0)) <> coalesce(NEW.nr_seq_status,0)) then
	
	select	nextval('oft_log_status_consulta_seq')
	into STRICT	nr_sequencia_w
	;
	
	insert into oft_log_status_consulta(
		nr_sequencia,
		nr_seq_status,
		nr_seq_consulta,
		dt_atualizacao,
		nm_usuario)
	values (
		nr_sequencia_w,
		NEW.nr_seq_status,
		OLD.nr_sequencia,
		LOCALTIMESTAMP,
		NEW.nm_usuario);
		
	insert	into	oft_status_consulta_hist(nr_sequencia,
						nr_seq_consulta,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_status,
						dt_status)
	values (nextval('oft_status_consulta_hist_seq'),
						NEW.nr_sequencia,
						LOCALTIMESTAMP,
						NEW.nm_usuario,
						LOCALTIMESTAMP,
						NEW.nm_usuario,
						NEW.nr_seq_status,
						LOCALTIMESTAMP);	
end if;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_oft_consulta_update() FROM PUBLIC;

CREATE TRIGGER oft_consulta_update
	AFTER UPDATE ON oft_consulta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_oft_consulta_update();

